const initialState = {
    goal: 0
}
export const goal = (state = initialState, action) => {
    switch (action.type) {
      case "SET_GOAL":
        return state.concat(action.goal);
      // return [...state, exercises: action.exercise];

      default:
        return state;
    }
  };
