import React, {Component} from 'react';
import './Book.css';

class Book extends Component {
    constructor(props){
        super(props)
        this.state = {
            counter: this.props.counter
        }
    }

//    componentDidMount= () => {
//       debugger
//     }


    handleClick = () => {
        //debugger
        this.setState({ counter: this.state.counter + 1 })
        //this.props.likeCounter(this.state)
       // debugger
      }

    callApi = () => {
        let data = {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              //'Authorization': sessionStorage.jwt
            }
          }
          fetch('http://localhost:3000/api/books', data)
            .then(response => {
                return response.json()
            })
            .then(books => console.log( books))
    }
render(){
    //debugger
    return(
        <div className="Book" onClick={this.props.clicked}>
            <h3>{this.props.title}</h3>
        <div className="Info">
                <div className="Author">{this.props.author}</div>
                    <img className="bookImage" src={this.props.img_url} alt={this.props.title}/>
                    <button onClick={() => this.handleClick(this.props.id)}>Like</button>
                    <button onClick={this.callApi}>Call Api</button>
                    <p>{this.state.counter}</p>
                </div>
            </div>
        )
    }

}

