const addBook = (state = [], action) => {

    switch (action.type) {
      case "ADD_BOOK":
      console.log('Current statee is: ', state);
      console.log(action.payload)
      return Object.assign({}, state, {
          state: action.payload
          })
      default:
      console.log("initial state is ", state)
        return state;
    }
  };
  export default addBook;
