const goal = (state = {goal: 0}, action) => {
  switch (action.type) {
    case "SET_GOAL":
    console.log('Current state.goal is: ', state.goal);
          console.log('Updating state.goal to: ', action.goal) //what is value
        return Object.assign({}, state, {
        goal: action.goal //what is value
        })

    default:
      return state;
  }
};
export default goal;
