        return fetch(`${API_URL}/books`)
            .then(response => response.json())
    },

    createBook(book) {
        const request = {
            method: 'POST',
            body: JSON.stringify({
                book: book
            }),
            headers: {
                'Content-Type': 'application/json',
            }
        };
